export type EmailProviders = 'magicLink' | 'dynamic' | 'emailOnly';
export type ConnectWithEmailOtpOptions = {
    provider?: EmailProviders;
};
export type ConnectWithEmailOtpContext = {
    connectWithEmail: (email: string, options?: ConnectWithEmailOtpOptions) => Promise<void>;
    verifyOneTimePassword: (oneTimePassword: string) => Promise<void>;
    retryOneTimePassword: () => Promise<void>;
};
export type ProviderVerificationHandler = {
    verify: (oneTimePassword: string) => Promise<{
        email: string;
        verified: boolean;
    }>;
    retry?: () => Promise<void>;
};
export type ProviderHandler = (email: string) => Promise<ProviderVerificationHandler>;
